            padding-right: $padding;
            padding-left: $padding;
        }
    }
}

$width: 0;

@if type-of($columns) == "number" {
    @if $columns < 1 {
        $width: percentage($columns);
    } @else {
        $width: percentage($columns / $dc-grid-column-count);
    }
} @else {
    @error "Wrong syntax for columns. It must be a number (of columns)";
}

@return $width;

